function jitter(){cardcontent.style.textShadow="4px 0px 1px",backcolor.style.mixBlendMode="luminosity"}function nojitter(){cardcontent.style.textShadow="0px 0px 1px",backcolor.style.mixBlendMode="normal"}function start(){max=9e3,min=4e3,sleep=Math.random()*(max-min)+min,setTimeout(jitter,sleep),setTimeout(nojitter,sleep+200),setTimeout(start,5e3)}var cardcontent=document.querySelector("#cardcontent"),backcolor=document.querySelector("html");start()