var cardcontent,backcolor;function isMobile(){return/Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(navigator.userAgent)}if(!isMobile()){function jitter(){cardcontent.style.textShadow="4px 0px 1px",backcolor.style.mixBlendMode="luminosity"}function nojitter(){cardcontent.style.textShadow="0px 0px 1px",backcolor.style.mixBlendMode="normal"}function start(){max=12e3,min=2e4,sleep=Math.random()*(max-min)+min,setTimeout(jitter,sleep),setTimeout(nojitter,sleep+200),setTimeout(start,5e3)}cardcontent=document.querySelector("#cardcontent"),backcolor=document.querySelector("html"),start()}